(358, 50, '_company_2_title', 'field_6366bac7962af'),
(359, 50, 'company_2_description', 'Criar valor entre as partes interessadas para perpetuação. Atuar com honestidade em todas as atividades e relacionamentos. Manter sempre o senso de urgência com erro zero. Desenvolver o avanço tecnológico em todos os processos. Valorizar o ambiente saudável, com respeito aos requisitos ambientais e a valorização das pessoas. '),
(360, 50, '_company_2_description', 'field_6366bad2962b0'),
(361, 50, 'company', '3'),
(362, 50, '_company', 'field_6366ba9f962ad'),
(363, 50, 'title_policy', 'Politica de Qualidade'),
(364, 50, '_title_policy', 'field_6366bb10962b2'),
(365, 50, 'text_policy', 'A SERVICORTE está comprometida com a qualidade no fornecimento de produtos e soluções em aços e metais planos e com o atendimento dos requisitos, através dos seguintes princípios:\r\n\r\nSatisfação das Partes Interessadas\r\n\r\nAtendimento dos Requisitos Regulamentares Estatutários\r\n\r\nMelhoria Contínua\r\n\r\nProcessos Controlados e Eficazes'),
(366, 50, '_text_policy', 'field_6366bb1a962b3'),
